import Role from "../models/Role";
import Deparment from "../models/Deparment";
import Position from "../models/Position";
import User from "../models/User";
import Dashboard from "../models/Dashboard";

export const createRoles = async () => {
  try {
    const count = await Role.estimatedDocumentCount();

    if (count > 0) return;

    const values = await Promise.all([
      new Role({ name: "user" }).save(),
      new Role({ name: "moderador" }).save(),
      new Role({ name: "admin" }).save(),
    ]);

    console.log(values);
  } catch (error) {
    console.error(error);
  }
};

export const createDepartments = async () => {
  try {
    const count = await Deparment.estimatedDocumentCount();

    if (count > 0) return;

    const values = await Promise.all([
      new Deparment({
        name: "IT",
        description: "IT and Development Department",
      }).save(),
      new Deparment({ name: "SETUP", description: "Setup machines" }).save(),
      new Deparment({
        name: "QUALITY",
        description: "Engineers and Inspectors",
      }).save(),
      new Deparment({
        name: "PRODUCTION",
        description: "Floor Production",
      }).save(),
      new Deparment({
        name: "AUTOMATION",
        description: "Robots and Gages",
      }).save(),
      new Deparment({ name: "MAINTENANCE", description: "Maintenance" }).save(),
      new Deparment({ name: "HR", description: "Human Resources" }).save(),
      new Deparment({ name: "GENERAL", description: "Others" }).save(),
    ]);

    console.log(values);
  } catch (error) {
    console.error(error);
  }
};

export const createPositions = async () => {
  try {
    const count = await Position.estimatedDocumentCount();

    if (count > 0) return;

    const values = await Promise.all([
      new Position({ name: "Supervisor", description: "Supervisor" }).save(),
      new Position({ name: "Leadhand", description: "Group Leadhand" }).save(),
      new Position({
        name: "Inspector",
        description: "Parts Inspector",
      }).save(),
      new Position({ name: "Technician", description: "Technician" }).save(),
      new Position({
        name: "Administrator",
        description: "Administrator",
      }).save(),
    ]);

    console.log(values);
  } catch (error) {
    console.error(error);
  }
};

export const createDashboard = async () => {
  try {
    const count = await Dashboard.estimatedDocumentCount();

    if (count > 0) return;

    const values = await Promise.all([
      new Dashboard({ name: "General", description: "Dashboard General Card", pos:"1" }).save(),
      new Dashboard({ name: "Setup", description: "Dashboard Setup Card", pos:"2" }).save(),
      new Dashboard({ name: "Quality", description: "Dashboard Quality Card", pos:"3" }).save(),
      new Dashboard({ name: "Production", description: "Dashboard Production Card", pos:"4" }).save(),
      new Dashboard({ name: "Other", description: "Dashboard Other Card", pos:"2" }).save(),
    ]);

    console.log(values);
  } catch (error) {
    console.error(error);
  }
};

export const createUser = async () => {
  try {
    const count = await User.estimatedDocumentCount();

    if (count > 0) return;

    const values = await Promise.all(
      new User({
        name: "Elden",
        lastName: "Lord",
        employeeNo: "1",
        username: "Eldenlord",
        password: "ranni1!",
        email: "it.admin@apgmexico.mx",
        roles: ["admin", "moderador", "user"],
        department: "GENERAL",
