import User from "../models/User";
import jwt from "jsonwebtoken";
import config from "../config";
import Role from "../models/Role";
import Department from "../models/Deparment";
import Position from "../models/Position";
import Dashboard from "../models/Dashboard";

//Usuario Nuevo
export const signUp = async (req, res) => {
  const {
    name,
    lastName,
    employeeNo,
    username,
    email,
    password,
    roles,
    department,
    position,
  } = req.body;

  const newUser = new User({
    name,
    lastName,
    employeeNo,
    username,
    email,
    password: await User.encryptPassword(password),
  });

  if (roles) {
    const foundRoles = await Role.find({ name: { $in: roles } });
    newUser.roles = foundRoles.map((role) => role._id);
  } else {
    const role = await Role.findOne({ name: "user" });
    newUser.roles = [role._id];
  }

  if (position) {
    const foundPositions = await Position.find({
      name: { $in: position },
    });
    newUser.position = foundPositions.map((position) => position._id);
  } else {
    const position = await Position.findOne({ name: "Supervisor" });
    newUser.position = [position._id];
  }

  if (department) {
    const foundDepartments = await Department.find({
      name: { $in: department },
    });
    newUser.department = foundDepartments.map((department) => department._id);
  } else {
    const department = await Department.findOne({ name: "GENERAL" });
    newUser.department = [department._id];
  }

  const savedUser = await newUser.save();

  console.log(savedUser);

  const token = jwt.sign({ id: savedUser._id }, config.SECRET, {
    expiresIn: 86400, // 24 Horas
  });

  res.status(200).json({ token });
};

// Usuario Existente

export const signIn = async (req, res) => {
  const userFound = await User.findOne({
    username: req.body.username,
  })
    .populate({ path: "roles", select: "name" })
    .populate({ path: "department", select: "name" })
    .populate({ path: "position", select: "name" });

  console.log(userFound);

  if (!userFound)
    return res
      .status(404)
      .json({ token: null, message: "User not found", status: "404" });

  const matchPassword = await User.comparePassword(
    req.body.password,
    userFound.password
  );

  if (!matchPassword)
    return res
      .status(404)
      .json({ token: null, message: "Invalid password", status: "404" });

  const token = jwt.sign({ id: userFound._id }, config.SECRET, {
    expiresIn: 86400,
  });

  const roles = await Role.find({ _id: { $in: userFound.roles } });
  let canApprove = "false";
  let canModify = "false";
  for (let i = 0; i < roles.length; i++) {
    if (roles[i].name === "KaizenRW" || roles[i].name === "admin") {
      canModify = "true";
    }
    if (roles[i].name === "KaizenApproval" || roles[i].name === "admin") {
      canApprove = "true";
    }
  }

  res.json({
    token,
    status: "200",
    message: "login complete",
    kaizenApproval: canApprove,
    kaizenModified: canModify,
  });
};

export const getDashboardCards = async (req, res) => {
  const cardsFound = await Dashboard.find();
  res.json({ status: "200", message: "Dashboard Loaded", body: cardsFound });
};

