import { Router } from "express";
import * as kaizenController from "../../controllers/Forms/Others/kaizen.controller";
const router = Router();
import { uploadKaizenImgs } from "../../middlewares/uploadKaizenImg";
import { authJwt } from "../../middlewares"

// Route to get All the kaizens
router.get("/Kaizen", authJwt.verifyToken, authJwt.isKaizenR, kaizenController.getKaizens);

// Route to get a Specific Kaizen by Id
router.get("/Kaizen/:kaizenId", authJwt.verifyToken, authJwt.isKaizenR, kaizenController.getKaizenById);

// Route to Post a New Kaizen
router.post("/Kaizen", uploadKaizenImgs, kaizenController.createKaizen);

// Route to modify all the fields in a specific kaizen
router.put("/Kaizen/:kaizenId", authJwt.verifyToken, authJwt.isKaizenRW, kaizenController.updateKaizen);

//Route to modify just the Status of a Kaizen
router.put("/Kaizen/Status/:kaizenId", authJwt.verifyToken, authJwt.isKaizenAprroval, kaizenController.updateKaizenStatus);

export default router;

