// Getting all Users/////////////////////////////////////////////////////////////////////////////////////////////////////////////
const getUsers = async (req, res) => {
  const origin = req.headers.origin;
  //console.log(origin)
  //res.header('Access-Control-Allow-Origin', origin);
  //res.header('Access-Control-Allow-Origin', ['https://www.axiompaperless.com', 'https://axiompaperless.com']);
