const {
  Role
} = require("../models/Role.js");

const Deparment = require("../models/Deparment.js");

const Position = require("../models/Position.js");

const User = require("../models/User.js");

const Dashboard = require("../models/Dashboard.js");

const Employees = require("../models/Employees.js");

const Forms = require("../models/Forms.js");

const {
  dataEmployees
} = require("./EmployeeRawData.js");

const {
  dataForms
} = require("./FormsRawData.js");

const Customer = require("../models/General/Customer.js");

const Parts = require("../models/Quality/Parts.js");

const {
  dataParts
} = require("./PartsRawData.js");

const Kaizen = require("../models/Others/Kaizen.js");

const Company = require("../models/Company.js");

const {
  dataPartsInfo
} = require("./PartsInfoRawData.js");

const PartsInfo = require("../models/Quality/PartsInfo.js");

const {
  dataMachine
} = require("./MachineRawData.js");

const Machine = require("../models/Setup/Machine.js"); //crear compañias/////////////////////////////////////////////////////////////////////////////////////////////


const createCompanys = async () => {
  try {
    const count = await Company.estimatedDocumentCount();
    if (count > 0) return;
    const values = await Promise.all([new Company({
      name: 'APG Mexico',
      description: '',
      direction: "",
      location: ""
    }).save(), new Company({
      name: 'Axiom',
      description: '',
      direction: "",
      location: ""
    }).save()]);
  } catch (error) {
    console.error(error);
  }
}; // crear dashboard////////////////////////////////////////////////////////////////////////////////////////////


const createDashboard = async () => {
  try {
    const count = await Dashboard.estimatedDocumentCount();
    if (count > 0) return;
    const values = await Promise.all([new Dashboard({
      name: "General",
      description: "Dashboard General Card",
      icon: "icon-general.png",
      back: "img-general.jpeg",
      pos: "1"
    }).save(), new Dashboard({
      name: "Setup",
      description: "Dashboard Setup Card",
      icon: "icon-setup.png",
      back: "img-setup.jpeg",
      pos: "2"
    }).save(), new Dashboard({
      name: "Quality",
      description: "Dashboard Quality Card",
      pos: "3",
      icon: "icon-quality.png",
      back: "img-quality.jpeg"
    }).save(), new Dashboard({
      name: "Production",
      description: "Dashboard Production Card",
      pos: "4",
      icon: "icon-production.png",
      back: "img-production.jpeg"
    }).save(), new Dashboard({
      name: "Other",
      description: "Dashboard Other Card",
      pos: "5",
      icon: "icon-other.png",
      back: "img-other.jpeg"
    }).save()]);
  } catch (error) {
    console.error(error);
  }
}; // Crear roles/////////////////////////////////////////////////////////////////////////////////////////////////////////


const createRoles = async () => {
  try {
    const count = await Role.estimatedDocumentCount();
    if (count > 0) return;
    const values = await Promise.all([new Role({
      name: "user",
      description: "Basic user role",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "moderador",
      description: "Moderador role",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "admin",
      description: "Admin role",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "GeneralR",
      description: "The user can open and read General dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "GeneralRW",
      description: "The user can open, read and write General dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "SetupR",
      description: "The user can open and read Setup dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "SetupRW",
      description: "The user can open, read and write Setup dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "QualityR",
      description: "The user can open and read Quality dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "QualityRW",
      description: "The user can open, read and write Quality dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "ProductionR",
      description: "The user can open and read Production dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "ProductionRW",
      description: "The user can open, read and write Production dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "OtherR",
      description: "The user can open and read Other dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "OtherRW",
      description: "The user can open, read and write Other dashboard",
      category: "Dashboard Roles"
    }).save(), new Role({
      name: "KaizenR",
      description: "The user can open and read Kaizens",
      category: "Kaizen Roles"
    }).save(), new Role({
      name: "KaizenRW",
      description: "The user can modify Kaizens",
      category: "Kaizen Roles"
    }).save(), new Role({
      name: "KaizenApproval",
      description: "The user can approve Kaizens",
      category: "Kaizen Roles"
    }).save()]);
  } catch (error) {
    console.error(error);
  }
}; // Crear departamentos/////////////////////////////////////////////////////////////////////////////////////////////////


const createDepartments = async () => {
  try {
    const count = await Deparment.estimatedDocumentCount();
    if (count > 0) return;
    const values = await Promise.all([new Deparment({
      name: 'Production',
