  try {
    const count = await Position.estimatedDocumentCount();
    if (count > 0) return;
    const values = await Promise.all([new Position({
      name: "Administrator",
      description: "Administrator"
    }).save(), new Position({
      name: 'Visual Quality Inspector',
      description: ' Visual Quality Inspector '
    }).save(), new Position({
      name: 'Quality Engineer',
      description: ' Quality Engineer '
    }).save(), new Position({
      name: 'General Counter',
      description: ' General Counter '
    }).save(), new Position({
