const verifyToken = async (req, res, next) => {
  try {
    const token = req.headers["x-access-token"];
    if (!token) return res.status(403).json({
      message: "No token provided",
      status: "403"
    });
    const decoded = jwt.verify(token, process.env.SECRET);
    req.userId = decoded.id;
    const user = await User.findById(req.userId, {
      password: 0
    });
    if (!user) return res.status(404).json({
      message: "User not found",
      status: "404"
    });
    next();
  } catch (error) {
    return res.status(401).json({
      message: "nO Unauthorized",
      status: "401"
    });
  }
}; //Verify Moderador Role (moderador role)////////////////////////////////////////////////////////////////////////////


const isModerator = async (req, res, next) => {
  const user = await User.findById(req.userId);
  const roles = await Role.find({
    _id: {
      $in: user.roles
    }
  });
  const rolesAxiom = await Role.find({
    _id: {
      $in: user.rolesAxiom
    }
  });
  const Access = [];
  const {
    CompanyId
  } = req.params;
  Access.company = await Company.find({
    _id: {
      $in: CompanyId
    }
  });

  if (Access.company[0].name === "APG Mexico") {
    for (let i = 0; i < roles.length; i++) {
      if (roles[i].name === "admin") {
        next();
        return;
      }

      if (roles[i].name === "moderador") {
        next();
        return;
      }
    }
  }

  if (Access.company[0].name === "Axiom") {
    for (let i = 0; i < rolesAxiom.length; i++) {
      if (rolesAxiom[i].name === "admin") {
        next();
        return;
      }

      if (rolesAxiom[i].name === "moderador") {
        next();
        return;
      }
    }
  }

  return res.status(403).json({
