const { Router } = require("express");
const router = Router();
const {
    createPart,
    udpateParts,
    getParts
} = require("../../controllers/Quality/parts.controller.js");
const {
    verifyToken,
    isQualityRW,
    isAutorized
} = require("../../middlewares/auth.Jwt.js");
const {
    signPartsInfo,
    updatePartInfo
} = require("../../controllers/Quality/partsInfo.controller.js");

///Route to create new part///
router.post(
    "/NewPart/:CompanyId",
    verifyToken,
    isAutorized,
    isQualityRW,
    createPart
);
///Route to update part number///
router.put("/UpdatePart/:partId/:CompanyId",
    verifyToken,
    isAutorized,
    isQualityRW,
