              // }],
              html: 'DO NOT REPLY BACK this email - Autogenerated <br> <br>' + `${message}` + '<br> <br> <img src="https://paperless-apg.s3.amazonaws.com/Uploads/EmailPic/Alerta2.jpg"/>'
            };
            transporter.sendMail(mailOptions, (error, info) => {
              if (error) {
                //return res
                //.status(206)
                //.json({message: "Email not Delivered: " + error, status:"206"});
                console.log('Error sending email: ', error);
