        status: status
      });
      const foundParts = await Parts.find({
        partnumber: {
          $in: partsinfo[i].partnumber
        }
      });
      newPartinfo.partnumber = foundParts.map(parts => parts._id);
      let savedPartInfo = await newPartinfo.save();
    }
  } catch (error) {}
