  if(Access.company[0].name==="APG Mexico"){
  for (let i = 0; i < roles.length; i++) {
    
    if (roles[i].name === "admin") {
      next();
      return;
    }

    if (roles[i].name === "moderador") {
      next();
      return;
    }
  }
  }
