const User = require("../models/User.js");

const jwt = require("jsonwebtoken");

const config = require("../config.js");

const {
  Role
} = require("../models/Role.js");

const Dashboard = require("../models/Dashboard.js");

const Employees = require("../models/Employees.js");

const Company = require("../models/Company.js"); // import User from "../models/User.js";
// import jwt from "jsonwebtoken";
// import config from "../config.js";
// import Role from "../models/Role.js";
// import Dashboard from "../models/Dashboard.js";
// import Employees from "../models/Employees.js";
// import Company from "../models/Company.js";
// import { isAdmin } from "../middlewares/auth.Jwt.js";
// import Department from "../models/Deparment.js";
// import Position from "../models/Position.js";
//Usuario Nuevo


const signUp = async (req, res) => {
  const {
    username,
    email,
    password,
    signature,
    roles,
    rolesAxiom,
    employee,
    companyAccess,
    company
  } = req.body;
  const newUser = new User({
    username,
    email,
    password: await User.encryptPassword(password),
    signature
  });

  if (roles) {
    const foundRoles = await Role.find({
      name: {
        $in: roles
      }
    });
    newUser.roles = foundRoles.map(role => role._id);
  }

  if (rolesAxiom) {
    const foundRoles = await Role.find({
      name: {
        $in: rolesAxiom
      }
    });
    newUser.rolesAxiom = foundRoles.map(role => role._id);
  }

  if (employee) {
    const foundEmployees = await Employees.find({
      numberEmployee: {
        $in: employee
      }
    });
    newUser.employee = foundEmployees.map(employee => employee._id);
  }

  if (companyAccess) {
    const foundCompany = await Company.find({
      name: {
        $in: companyAccess
      }
    });
    newUser.companyAccess = foundCompany.map(company => company._id);
  }

  if (company) {
    const foundCompany = await Company.find({
