const Customer = require("../../../models/General/Customer.js");

const Parts = require("../../../models/Quality/Parts.js");

const DeviationRequest = require("../../../models/General/DeviationRequest.js");

const User = require("../../../models/User.js");

const DeviationRiskAssessment = require("../../../models/General/DeviationRiskAssessment.js");

const Company = require("../../../models/Company.js");

const fs = require("fs"); // import Customer from "../../../models/General/Customer.js";
// import Parts from "../../../models/Quality/Parts.js";
// import DeviationRequest from "../../../models/General/DeviationRequest.js";
// import User from "../../../models/User.js";
// import DeviationRiskAssessment from "../../../models/General/DeviationRiskAssessment.js";
// import Company from "../../../models/Company.js";
// import fs from "fs";
// import Deparment from "../../../models/Deparment.js";


const createDeviationRequest = async (req, res) => {
  const {
    deviationDate,
    deviationType,
    supplier,
    customer,
    requestBy,
    implementationDate,
    implementationTime,
    parts,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    priority,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus,
    deviationRisk,
    company
  } = req.body;
  const newDeviationReq = new DeviationRequest({
    deviationDate,
    deviationType,
    supplier,
    requestBy,
    implementationDate,
    implementationTime,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    priority,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus,
    deviationRisk
  });

  if (requestBy) {
    const foundUsers = await User.find({
      username: {
        $in: requestBy
      }
    });
    newDeviationReq.requestBy = foundUsers.map(user => user._id);
  }

  if (customer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: customer
      }
    });
    newDeviationReq.customer = foundCustomers.map(customer => customer._id);
  }

  if (parts) {
    const foundParts = await Parts.find({
      partnumber: {
        $in: parts
      }
    });
    newDeviationReq.parts = foundParts.map(parts => parts._id);
  }

  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    newDeviationReq.company = foundCompany.map(company => company._id);
  }

  if (!requestBy || !customer || !parts || !company) {
    return res.status(403).json({
      message: "Deviation not saved",
      status: "403"
    });
  }

  const count = await DeviationRequest.estimatedDocumentCount();

  if (count > 0) {
    const deviations = await DeviationRequest.find().sort({
      consecutive: -1
    }).limit(1);
    newDeviationReq.consecutive = deviations[0].consecutive + 1;
  } else {
    newDeviationReq.consecutive = 1;
  }

  newDeviationReq.deviationNumber = "APG-" + 2022 + "-" + `${Number(newDeviationReq.consecutive)}`.padStart(3, "0");
  const savedDeviationRequest = await newDeviationReq.save();

  if (!savedDeviationRequest) {
    res.status(403).json({
      status: "403",
      message: "Deviation not Saved",
      body: ""
    });
  }

  res.json({
    status: "200",
    message: "Deviation request created",
    savedDeviationRequest
  });
}; // Getting all deviations


const getDeviationRequest = async (req, res) => {
  const {
    CompanyId
  } = req.params;

  if (CompanyId.length !== 24) {
    return;
  }

  const company = await Company.find({
    _id: {
      $in: CompanyId
    }
  });

  if (!company) {
    return;
  }

  const deviations = await DeviationRequest.find({
    company: {
      $in: CompanyId
    }
  }).sort({
    consecutive: -1
  }).populate({
    path: 'customer'
  }).populate({
    path: 'requestBy'
  }).populate({
    path: 'requestBy',
    populate: {
      path: "employee",
      model: "Employees",
      populate: {
        path: "department",
        model: "Department"
      }
    }
  }).populate({
    path: 'parts'
  });
  res.json({
    status: "200",
    message: "Deviations Loaded",
    body: deviations
  });
}; // Getting deviation by Id


const getDeviationById = async (req, res) => {
  const foundDeviation = await DeviationRequest.findById(req.params.deviationId).populate({
    path: 'customer'
  }).populate({
    path: 'requestBy'
  }).populate({
    path: 'requestBy',
    populate: {
      path: "employee",
      model: "Employees",
      populate: {
        path: "department",
        model: "Department"
      }
    }
  }).populate({
    path: 'parts'
  });

  if (!foundDeviation) {
    res.status(403).json({
      status: "403",
      message: "Deviation not Founded",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "Deviation Founded",
    body: foundDeviation
  });
}; // Updating deviation status


const updateDeviation = async (req, res) => {
  const {
    deviationId
  } = req.params;
  const {
    deviationGranted,
    qualitySign,
    dateQualitySign,
    qualitySignStatus,
    seniorSign,
    dateSeniorSign,
    seniorSignStatus
  } = req.body;
  const updatedDeviation = await DeviationRequest.updateOne({
    _id: deviationId
  }, {
    $set: {
      deviationGranted,
      qualitySign,
      dateQualitySign,
      qualitySignStatus,
      seniorSign,
      dateSeniorSign,
      seniorSignStatus
    }
  });

  if (!updatedDeviation) {
    res.status(403).json({
      status: "403",
      message: "Deviation not Updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "Deviation Status Updated ",
    body: updatedDeviation
  });
}; //method to update deviation request 


const updateDeviationReq = async (req, res) => {
  const {
    deviationId
  } = req.params;
  const newCustomer = req.body.customer;
  const newParts = req.body.parts;
  const newDeviation = [];
  newDeviation.deviationDate = req.body.deviationDate;
  newDeviation.deviationType = req.body.deviationType;
  newDeviation.supplier = req.body.supplier;
