  if (foundEmployees.length === 0) {
    return res.json({
      status: "403",
      message: "not employee founded",
      body: ""
    });
  }

  const employee = foundEmployees.map(employee => employee._id);
  const {
    parameterSetting,
    revisionDate,
    repairMold,
    labelColor,
    ifRepair,
    orderJob,
    resinVerifiedDried,
    numberDryer,
    switchConected,
    comments,
    purgeMachine,
    containerRed,
    alarmsProgramed,
    moldWather,
    temperatures,
    transportBand,
    counterMachine,
    settingRobot,
    formatEmp,
    noCheckValidation,
    status
  } = req.body;
  const updatedAssemblyStartTechnical = await AssemblyStartTechnical.updateOne({
    _id: assemblyStartTechnicalId
  }, {
    $set: {
      parameterSetting,
      revisionDate,
      repairMold,
      labelColor,
      ifRepair,
      orderJob,
      resinVerifiedDried,
      numberDryer,
      switchConected,
      comments,
      purgeMachine,
      containerRed,
      alarmsProgramed,
      moldWather,
      temperatures,
      transportBand,
      counterMachine,
      settingRobot,
      formatEmp,
      employee,
      noCheckValidation,
      status
    }
  });

  if (!updatedAssemblyStartTechnical) {
    res.status(403).json({
      status: "403",
      message: "assembly start not updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "assembly start updated",
