        },
        materialsRemoved:{
            type:String,
        },
        comments:{
            type:String,
        },
        packingComponents:{
            type:String,
        },
        workStation:{
            type:String,
        },
        containerScrap:{
            type:String,
        },
        docEstation:{
            type:String,
        },
        correctComponent:{
            type:String,
        },
