  }
};
//create parts info///////////////////////////////////////////////////////////////////////////////////////////////
const createPartsInfo = async () => {
  let partsinfo = dataPartsInfo;
  let status = true;
  const foundCompany = await Company.find({
    name: { $in: "APG Mexico" },
  });
  const company = foundCompany.map((company) => company._id);
  try {
    const count = await PartsInfo.estimatedDocumentCount();
