    if (rolesAxiom[i].name === "QualityASL" || rolesAxiom[i].name === "QualityASM" || rolesAxiom[i].name === "QualityASH" || rolesAxiom[i].name === "SeniorManagement") {
      userAccessAXG[5] = "true";
    }
    if (rolesAxiom[i].name === "QualityASM") {
