// Controlador para el Kaizen
const Kaizen = require("../../../models/Others/Kaizen.js");

const Company = require("../../../models/Company.js");

const AWS = require('aws-sdk');

const dotenv = require('dotenv');

dotenv.config({
  path: "C:\\api-paperless-apg\\src\\.env"
});
AWS.config.update({
  region: process.env.S3_BUCKET_REGION,
  apiVersion: 'latest',
  credentials: {
    accessKeyId: process.env.S3_ACCESS_KEY,
    secretAccessKey: process.env.S3_SECRET_ACCESS_KEY
  }
});
const s3 = new AWS.S3(); //Create new kaizen/////////////////////////////////////////////////////////////////////////////////////////////////////////

const createKaizen = async (req, res) => {
  const {
    kaizenName,
    date,
    createdBy,
    area,
    implementArea,
    implementDate,
    takenPlant,
    teamKaizen,
    money,
    space,
    security,
    ergonomy,
    fiveS,
    environment,
    process,
    motivation,
    other,
    beforeKaizen,
    afterKaizen,
    status,
    montlyRank,
    observations,
    lastModifyBy,
    implementationCost,
    company
  } = req.body; //Retreiving the data for each Before Kaizen Image and adding to the schema

  let kaizenImagesB = [];

  if (req.files["kaizenImagesB"]) {
    if (req.files["kaizenImagesB"].length > 0) {
      kaizenImagesB = req.files["kaizenImagesB"].map(file => {
        return {
          img: file.key
        };
      });
    }
  } //Retreiving the data for each Before Kaizen Image and adding to the schema


  let kaizenImagesA = [];

  if (req.files["kaizenImagesA"]) {
    if (req.files["kaizenImagesA"].length > 0) {
      kaizenImagesA = req.files["kaizenImagesA"].map(file => {
        return {
          img: file.key
        };
      });
    }
  }

  const kaizen = new Kaizen({
    kaizenName,
    date,
    createdBy,
    area,
    implementArea,
    implementDate,
    takenPlant,
    teamKaizen,
    money,
    space,
    security,
    ergonomy,
    fiveS,
    environment,
    process,
    motivation,
    other,
    beforeKaizen,
    afterKaizen,
    status,
    montlyRank,
    observations,
    lastModifyBy,
    implementationCost,
    kaizenImagesB,
    kaizenImagesA
  });

  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    kaizen.company = foundCompany.map(company => company._id);
  }

  const kaizens = await Kaizen.find({
    company: {
      $in: req.body.company
    }
  }).sort({
    consecutive: -1
  }).limit(1);

  if (kaizens.length === 0) {
    kaizen.consecutive = 1;
  } else {
    kaizen.consecutive = kaizens[0].consecutive + 1;
  }

  kaizen.save((error, kaizen) => {
    if (error) return res.status(400).json({
      status: "400",
      message: error
    });

    if (kaizen) {
      res.json({
        status: "200",
        message: "Kaizen Created",
        body: kaizen
      });
    }
  });
}; // Getting all Kaizens//////////////////////////////////////////////////////////////////////////////////////////////////////


const getKaizens = async (req, res) => {
  const {
    CompanyId
  } = req.params;

  if (CompanyId.length !== 24) {
    return;
  }

  const company = await Company.find({
    _id: {
      $in: CompanyId
    }
  });

  if (!company) {
    return;
  }

  const kaizens = await Kaizen.find({
    company: {
      $in: CompanyId
    }
  }).sort({
    consecutive: -1
  });
  res.json({
    status: "200",
    message: "Kaizens Loaded",
    body: kaizens
  });
}; // Getting Kaizen by Id////////////////////////////////////////////////////////////////////////////////////////////////////////


const getKaizenById = async (req, res) => {
  const foundKaizen = await Kaizen.findById(req.params.kaizenId);

  if (!foundKaizen) {
    res.status(403).json({
      status: "403",
      message: "Kaizen not Founded",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "Kaizen Founded",
    body: foundKaizen
  });
}; // Getting Kaizens Filtered///////////////////////////////////////////////////////////////////////////////////////////////////


const getKaizensFiltered = async (req, res) => {
  const {
    start,
    end,
    area,
    status,
    createdBy,
    montlyRank,
    company
  } = req.body;
  let options = {}; //Date range filter

  if (req.body.start && req.body.end) {
    if (!options["date"]) options["date"] = {};
    options["date"]["$gte"] = new Date(start);
    options["date"]["$lte"] = new Date(end);
  } // Filter by Area


  if (area) {
    options["area"] = area;
  } // Filter by Status


  if (status) {
    options["status"] = status;
  } // Filter Created By


  if (createdBy) {
    options["createdBy"] = createdBy;
  } // Filter MontlyRank


  if (montlyRank) {
    options["montlyRank"] = montlyRank;
  } // Filter Company


  if (company) {
    options["company"] = company;
  }

  const kaizens = await Kaizen.find(options).sort({
    date: -1
  });
  res.json({
    status: "200",
    message: "Kaizens Loaded New",
    body: kaizens
  });
}; // Updating the Kaizen All data//////////////////////////////////////////////////////////////////////////////////////////////


const updateKaizen = async (req, res) => {
  const {
    kaizenId
  } = req.params;
  const {
    kaizenName,
    createdBy,
    area,
    implementArea,
    implementDate,
    takenPlant,
    teamKaizen,
    money,
    space,
    security,
    ergonomy,
    fiveS,
    environment,
    process,
    motivation,
    other,
    beforeKaizen,
    afterKaizen,
    status,
    lastModifyBy,
    implementationCost
  } = req.body;
  const updatedKaizen = await Kaizen.updateOne({
    _id: kaizenId
  }, {
    $set: {
      kaizenName,
      createdBy,
      area,
      implementArea,
      implementDate,
      takenPlant,
      teamKaizen,
      money,
      space,
      security,
      ergonomy,
      fiveS,
      environment,
      process,
      motivation,
      other,
      beforeKaizen,
      afterKaizen,
      status,
      lastModifyBy,
      implementationCost
    }
  });

  if (!updatedKaizen) {
    res.status(403).json({
      status: "403",
      message: "Kaizen not Updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "Kaizen Updated ",
    body: updatedKaizen
  });
}; // Updating the Kaizen status/////////////////////////////////////////////////////////////////////////////////


const updateKaizenStatus = async (req, res) => {
  const {
    kaizenId
  } = req.params;
  const {
    status,
    observations,
    montlyRank,
    lastModifyBy
  } = req.body;
  const updatedKaizenStatus = await Kaizen.updateOne({
    _id: kaizenId
  }, {
    $set: {
      status,
      observations,
      montlyRank,
      lastModifyBy
    }
  });

  if (!updatedKaizenStatus) {
    res.status(403).json({
      status: "403",
      message: "Kaizen Status not Updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "Kaizen Status Updated ",
    body: updatedKaizenStatus
  });
}; // Function to modify the Images from a Kaizen/////////////////////////////////////////////////////////////////////


const modifyKaizenImg = async (req, res) => {
  const {
    kaizenId
  } = req.params; //Getting Previous Images

  const foundPrevKaizen = await Kaizen.findById(kaizenId); // Deleting Images from Folder for KaizenB

  const prevKaizenImagesB = foundPrevKaizen.kaizenImagesB;

  if (prevKaizenImagesB) {
    // Validating if there are Images in the Field
    if (prevKaizenImagesB.length > 0) {
      prevKaizenImagesB.map(file => {
        // Delete File from Folder
        const params = {
          Bucket: process.env.S3_BUCKET_NAME + "/Uploads/KaizenImgs",
          Key: file.img
        };

        try {
          s3.deleteObject(params, function (err, data) {
            if (err) console.log(err);
          });
        } catch (error) {
          res.status(403).json({
            status: "403",
            message: error,
            body: ""
          });
        }
      });
    }
  } // Deleting Images from Folder for KaizenA


  const prevKaizenImagesA = foundPrevKaizen.kaizenImagesA;

  if (prevKaizenImagesA) {
    // Validating if there are Images in the Field
    if (prevKaizenImagesA.length > 0) {
      prevKaizenImagesA.map(file => {
        // Delete File from Folder
        const params = {
          Bucket: process.env.S3_BUCKET_NAME + "/Uploads/KaizenImgs",
          Key: file.img
        };

        try {
          s3.deleteObject(params, function (err, data) {
            if (err) console.log(err);
          });
        } catch (error) {
          res.status(403).json({
            status: "403",
            message: error,
            body: ""
          });
        }
      });
    }
  } // Setting the Fields Empty in the DB


  const updateClearImgKaizen = await Kaizen.updateOne({
    _id: kaizenId
  }, {
    $set: {
      kaizenImagesB: [],
      kaizenImagesA: []
    }
  });

  if (!updateClearImgKaizen) {
    res.status(403).json({
      status: "403",
      message: "Kaizen not Updated - updateClearImgKaizen",
      body: ""
    });
  } //Retreiving the data for each Before Kaizen Image and adding to the schema


  let kaizenImagesB = [];

  if (req.files["kaizenImagesB"]) {
    if (req.files["kaizenImagesB"].length > 0) {
      kaizenImagesB = req.files["kaizenImagesB"].map(file => {
        return {
          img: file.key
        };
      });
    }
  } //Retreiving the data for each Before Kaizen Image and adding to the schema


  let kaizenImagesA = [];

  if (req.files["kaizenImagesA"]) {
    if (req.files["kaizenImagesA"].length > 0) {
      kaizenImagesA = req.files["kaizenImagesA"].map(file => {
        return {
          img: file.key
        };
      });
    }
  } // Updating the new Img Names in the fields from the DB


  const updateImgKaizen = await Kaizen.updateOne({
    _id: kaizenId
  }, {
    $set: {
      kaizenImagesB,
      kaizenImagesA
    }
  });

  if (!updateImgKaizen) {
    res.status(403).json({
      status: "403",
      message: "Kaizen not Updated - updateImgKaizen",
      body: ""
    });
  }

  const foundKaizenNew = await Kaizen.findById(kaizenId);
  res.status(200).json({
    status: "200",
    message: "Images Updated",
    body: foundKaizenNew
  });
}; //delete kaizen/////////////////////////////////////////////////////////////////////////////////////////////////////


const deleteKaizen = async (req, res) => {
  const {
    kaizenId
  } = req.params;
  const foundPrevKaizen = await Kaizen.findById(kaizenId); // Deleting Images from Folder for KaizenB

  const prevKaizenImagesB = foundPrevKaizen.kaizenImagesB;

  if (prevKaizenImagesB) {
    // Validating if there are Images in the Field
    if (prevKaizenImagesB.length > 0) {
      prevKaizenImagesB.map(file => {
        // Delete File from Folder
        const params = {
          Bucket: process.env.S3_BUCKET_NAME + "/Uploads/KaizenImgs",
          Key: file.img
        };

        try {
          s3.deleteObject(params, function (err, data) {
            if (err) console.log(err);
          });
        } catch (error) {
          res.status(403).json({
            status: "403",
            message: error,
            body: ""
          });
        }
      });
    }
  } // Deleting Images from Folder for KaizenA


  const prevKaizenImagesA = foundPrevKaizen.kaizenImagesA;

  if (prevKaizenImagesA) {
    // Validating if there are Images in the Field
    if (prevKaizenImagesA.length > 0) {
      prevKaizenImagesA.map(file => {
        // Delete File from Folder
        const params = {
          Bucket: process.env.S3_BUCKET_NAME + "/Uploads/KaizenImgs",
          Key: file.img
        };

        try {
          s3.deleteObject(params, function (err, data) {
            if (err) console.log(err);
          });
        } catch (error) {
          res.status(403).json({
            status: "403",
            message: error,
            body: ""
          });
        }
      });
    }
  }

  Kaizen.findById(kaizenId, function (err, kaizen) {
    if (err) {
      res.status(503).json({
        status: "403",
        message: err
      });
      return;
    }

    kaizen.remove(res.status(200).json({
      status: "200",
      message: 'The kaizen has been deleted'
    }));
  });
};

module.exports = {
  createKaizen,
  getKaizens,
  getKaizenById,
  getKaizensFiltered,
  updateKaizen,
  updateKaizenStatus,
  modifyKaizenImg,
  deleteKaizen
};
