});
const s3 = new AWS.S3(); //create deviation request//////////////////////////////////////////////////////////////////////////////////////

const createDeviationRequest = async (req, res) => {
  const {
    deviationDate,
    deviationType,
    supplier,
    customer,
    requestBy,
    implementationDate,
    implementationTime,
    parts,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    priority,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus,
    deviationRisk,
    company
  } = req.body;
  const newDeviationReq = new DeviationRequest({
    deviationDate,
    deviationType,
    supplier,
    requestBy,
    implementationDate,
    implementationTime,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    priority,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus,
    deviationRisk
  });

  if (requestBy) {
    const foundUsers = await User.find({
      username: {
        $in: requestBy
      }
    });
    newDeviationReq.requestBy = foundUsers.map(user => user._id);
  }

  if (customer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: customer
      }
    });
    newDeviationReq.customer = foundCustomers.map(customer => customer._id);
  }

  if (parts) {
    const foundParts = await Parts.find({
      partnumber: {
        $in: parts
      }
    });
    newDeviationReq.parts = foundParts.map(parts => parts._id);
  }

  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    newDeviationReq.company = foundCompany.map(company => company._id);
  }

  if (!requestBy || !customer || !parts || !company) {
    return res.status(403).json({
      message: "Deviation not saved",
      status: "403"
    });
  }

  const count = await DeviationRequest.estimatedDocumentCount();

  if (count > 0) {
    const deviations = await DeviationRequest.find().sort({
      consecutive: -1
    }).limit(1);
    newDeviationReq.consecutive = deviations[0].consecutive + 1;
  } else {
    newDeviationReq.consecutive = 1;
  }

  newDeviationReq.deviationNumber = "APG-" + 2022 + "-" + `${Number(newDeviationReq.consecutive)}`.padStart(3, "0");
  const savedDeviationRequest = await newDeviationReq.save();

  if (!savedDeviationRequest) {
    res.status(403).json({
      status: "403",
      message: "Deviation not Saved",
      body: ""
    });
  }

  res.json({
    status: "200",
    message: "Deviation request created",
    savedDeviationRequest
  });
}; // Getting all deviations request/////////////////////////////////////////////////////////////////////////////////////////////////////


