const Parts = require("../../models/Quality/Parts.js");

const Customer = require("../../models/General/Customer.js");

const Company = require("../../models/Company.js"); //Create new part number////////////////////////////////////////////////////////////////////////////////////////////


const createPart = async (req, res) => {
  const {
    partnumber,
    partName,
    partEcl,
    customer,
    company
  } = req.body;
  const newPart = new Parts({
    partnumber,
    partName,
    partEcl
  });

  if (customer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: customer
      }
    });
    newPart.customer = foundCustomers.map(customer => customer._id);
  }

  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    newPart.company = foundCompany.map(company => company._id);
  }

  const savedPart = await newPart.save();
  res.json({
    status: "200",
    message: "Part created",
    savedPart
  });
}; //update parts//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


const udpateParts = async (req, res) => {
  const {
    partId
  } = req.params;
  const UpdPart = [];
  UpdPart.partnumber = req.body.partnumber;
  UpdPart.partName = req.body.partName;
  UpdPart.partEcl = req.body.partEcl;
  newCustomer = req.body.customer;

  if (newCustomer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: newCustomer
      }
    });
    UpdPart.customer = foundCustomers.map(customer => customer._id);
  }

  const {
    partnumber,
    partName,
    partEcl,
    customer
  } = UpdPart;
  const updatedPart = await Parts.updateOne({
    _id: partId
  }, {
    $set: {
      partnumber,
      partName,
      partEcl,
      customer
    }
  });

  if (!updatedPart) {
    res.status(403).json({
      status: "403",
      message: "part not updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "part updated",
    body: updatedPart
  });
}; //Getting all parts///////////////////////////////////////////////////////////////////////////////////////////////////////////////////


const getParts = async (req, res) => {
  const {
    CompanyId
  } = req.params;

  if (CompanyId.length !== 24) {
    return;
  }

  const company = await Company.find({
    _id: {
      $in: CompanyId
    }
  });

  if (!company) {
    return;
  }

  const parts = await Parts.find({
    company: {
      $in: CompanyId
    }
  }).populate({
    path: "customer"
  }).sort({
    partName: 1
  });
  res.json({
    status: "200",
    message: "Parts loaded",
    body: parts
  });
};

module.exports = {
  createPart,
  udpateParts,
  getParts
};
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

