      $set: {
        verWatherMold,
        temperatures,
        lastPieces,
        cleanMold,
        watherConection,
        machinePurge,
        labelEnclosed,
        numberOrder,
        shootCounter,
        employeeT,
        dateT,
        hours,
        lastPiecesTaken,
        removeDocument,
        motiveStopMachine,
        employeeQ,
        dateQ,
        tools,
        workStationClean,
        formatAcumulation,
        aditionalComments,
        employeeP,
        dateP,
        status,
        noCheckValidation,
      },
    }
  );
  if (!updatedEndRun) {
    res.status(403).json({ status: "403", message: "change mold not updated", body: "" });
  }
  res.status(200).json({ status: "200", message: "change mold updated", body: updatedEndRun });
}
//method to get end run///////////////////////////////////////////////////////////////////////////////////////////////////////////////
const getEndRun = async (req, res) => {
  const endRun = await EndRun.find().sort({
    verWatherMold: 1,
  });
  res.json({ status: "200", message: "change mold loaded", body: endRun });
}

module.exports = {
  createEndRun,
  updateEndRun,
  getEndRun
};

