const User = require("../models/User.js");

const jwt = require("jsonwebtoken");

const Role = require("../models/Role.js");

const Dashboard = require("../models/Dashboard.js");

const Employees = require("../models/Employees.js");

const Company = require("../models/Company.js");

const Signature = require("../models/Signatures.js");

const PushToken = require("../models/PushToken.js");

console.log("ENV:", process.env.FIREBASE_CREDENTIALS_JSON ? "✅ cargada" : "❌ no encontrada"); //Crear un nuevo usuario///////////////////////////////////////////////////////////////////////////////////////////////////////////

const signUp = async (req, res) => {
  const {
    username,
    email,
    password,
    signature,
    roles,
    rolesAxiom,
    employee,
    companyAccess,
    company
  } = req.body; //guardar firma

  const newSignature = new Signature({
    username,
    signature
  });
  const savedSignature = await newSignature.save();
  const newUser = new User({
    username,
    email,
    password: await User.encryptPassword(password)
  });

  if (savedSignature) {
    const foundSignature = await Signature.find({
      username: {
        $in: username
      }
    });
    newUser.signature = foundSignature.map(signature => signature._id);
  } //Buscar roles en db y asignar a roles apg


  if (roles) {
    const foundRoles = await Role.find({
      name: {
        $in: roles
      }
    });
    newUser.roles = foundRoles.map(role => role._id);
  } //Buscar roles en db y asignar a roles axiom


  if (rolesAxiom) {
    const foundRoles = await Role.find({
      name: {
        $in: rolesAxiom
      }
    });
    newUser.rolesAxiom = foundRoles.map(role => role._id);
  } //buscar empleado y asisnar id de empleado a user


  if (employee) {
    const foundEmployees = await Employees.find({
      numberEmployee: {
        $in: employee
      }
    });
    newUser.employee = foundEmployees.map(employee => employee._id);
  } //compañia a la que puede tener acceso el usuario


  if (companyAccess) {
    const foundCompany = await Company.find({
      name: {
        $in: companyAccess
      }
    });
    newUser.companyAccess = foundCompany.map(company => company._id);
  } //compañia a la que pertenece el usuario


  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    newUser.company = foundCompany.map(company => company._id);
  }

  const savedUser = await newUser.save();
  const token = jwt.sign({
    id: savedUser._id
  }, process.env.SECRET, {
    expiresIn: 86400 // 24 Horas
    // expiresIn: 5,

  });
  res.json({
    status: "200",
    message: "User created"
  }); // res.status(200).json({ token });
}; //Crear nuevo rol//////////////////////////////////////////////////////////////////////////////////////////////////////////


const newRole = async (req, res) => {
  const {
    name,
    description,
    category
  } = req.body;
  const newRole = new Role({
    name,
    description,
    category
  });
  const savedRole = await newRole.save();
  res.json({
    status: "200",
    message: "Role created"
  });
}; //Login de usuario existente//////////////////////////////////////////////////////////////////////////////////////////////


const signIn = async (req, res) => {
  const userFound = await User.findOne({
    username: req.body.username
  }).populate({
    path: "roles",
    select: "name"
  }).populate({
    path: "companyAccess"
  }).populate({
    path: "company"
  }).populate({
    path: "employee"
  }); //comparar si existe el usuario

  if (!userFound) return res.status(404).json({
    token: null,
    message: "User not found",
    status: "404"
  });
  const matchPassword = await User.comparePassword(req.body.password, userFound.password); //Verificar que el usuario no este deshabilitado

  if (userFound.employee[0].active === false) return res.status(404).json({
    token: null,
    message: "User disabled",
    status: "404"
  }); //Verificar que la contraseña sea correcta

  if (!matchPassword) return res.status(404).json({
    token: null,
    message: "Invalid password",
