          });
        } catch (error) {
          res.status(403).json({
            status: "403",
            message: error,
            body: ""
          });
        }
      });
    }
  } // Setting the Fields Empty in the DB


  const updateClearImgKaizen = await Kaizen.updateOne({
    _id: kaizenId
  }, {
    $set: {
      kaizenImagesB: [],
