const DeviationRequest = require("../../../models/General/DeviationRequest.js");

const User = require("../../../models/User.js");

const DeviationRiskAssessment = require("../../../models/General/DeviationRiskAssessment.js");

const Company = require("../../../models/Company.js");

const AWS = require('aws-sdk');

const {
  sendEmailMiddlewareResponse
} = require("../../../middlewares/mailer.js");

AWS.config.update({
  region: process.env.S3_BUCKET_REGION,
  apiVersion: 'latest',
  credentials: {
    accessKeyId: process.env.S3_ACCESS_KEY,
    secretAccessKey: process.env.S3_SECRET_ACCESS_KEY
  }
});
const s3 = new AWS.S3(); //create deviation request//////////////////////////////////////////////////////////////////////////////////////

const createDeviationRequest = async (req, res, next) => {
  const {
    deviationNumber,
    deviationDate,
    deviationType,
    supplier,
    customer,
    requestBy,
    implementationDate,
    implementationTime,
    parts,
    applyTo,
    machineNo,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    severity,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus,
    deviationRisk,
    company
  } = req.body;
  const newDeviationReq = new DeviationRequest({
    deviationNumber,
    deviationDate,
    deviationType,
    supplier,
    requestBy,
    implementationDate,
    implementationTime,
    applyTo,
    machineNo,
    sectionTwo,
    termDevRequest,
    quantity,
    timePeriodStart,
    timePeriodEnd,
    other,
    deviationGranted,
    otherGranted,
    qualitySign,
    dateQualitySign,
    seniorSign,
    dateSeniorSign,
    customerSign,
    dateCustomerSign,
    comments,
    severity,
    qualitySignStatus,
    seniorSignStatus,
    customerSignStatus,
    deviationStatus
  });

  if (deviationRisk) {
    const foundRiskAssessment = await DeviationRiskAssessment.find({
      deviationNumber: {
        $in: deviationRisk
      }
    });
    newDeviationReq.deviationRisk = foundRiskAssessment.map(risk => risk._id);
  }

  if (requestBy) {
    const foundUsers = await User.find({
      username: {
        $in: requestBy
      }
    });
    newDeviationReq.requestBy = foundUsers.map(user => user._id);
  }

  if (customer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: customer
      }
    });
    newDeviationReq.customer = foundCustomers.map(customer => customer._id);
  }

  if (parts) {
    const foundParts = await Parts.find({
      partnumber: {
        $in: parts
      }
    });
    newDeviationReq.parts = foundParts.map(parts => parts._id);
  }

  if (company) {
    const foundCompany = await Company.find({
      _id: {
        $in: company
      }
    });
    newDeviationReq.company = foundCompany.map(company => company._id);
  }

  if (!requestBy || !customer || !parts || !company) {
    return res.status(403).json({
      message: "Deviation not saved",
      status: "403"
    });
  } //var count = 1
