  const {
    partnumber,
    partName,
    partEcl,
    customer,
    assemblyPartNumber,
    assemblyPartDesc,
    mould,
    status
  } = req.body;
  const newPart = new Parts({
    partnumber,
    partName,
    partEcl,
    assemblyPartNumber,
    assemblyPartDesc,
    mould,
    status
  }); // console.log(req.params)
  // console.log(CompanyId)

  if (customer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: customer
      }
    });
    newPart.customer = foundCustomers.map(customer => customer._id);
  }

  if (CompanyId) {
    const foundCompany = await Company.find({
      _id: {
        $in: CompanyId
      }
    });
    newPart.company = foundCompany.map(company => company._id);
  }

  newPart.save((error, newPart) => {
    if (error) return res.status(400).json({
      status: "400",
      message: error
    });

    if (newPart) {
      res.json({
        status: "200",
        message: "Part Created",
        body: newPart
      });
    }
  });
}; //update parts//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


const udpateParts = async (req, res) => {
  const {
    partId
  } = req.params;
  const UpdPart = [];
  UpdPart.partnumber = req.body.partnumber;
  UpdPart.partName = req.body.partName;
  UpdPart.partEcl = req.body.partEcl;
  UpdPart.assemblyPartNumber = req.body.assemblyPartNumber;
  UpdPart.assemblyPartDesc = req.body.assemblyPartDesc;
  UpdPart.mould = req.body.mould;
  UpdPart.status = req.body.status;
  newCustomer = req.body.customer;

  if (newCustomer) {
    const foundCustomers = await Customer.find({
      name: {
        $in: newCustomer
      }
    });
    UpdPart.customer = foundCustomers.map(customer => customer._id);
  }

  const {
    partnumber,
    partName,
    partEcl,
    customer,
    assemblyPartNumber,
    assemblyPartDesc,
    mould,
    status
  } = UpdPart;
  const updatedPart = await Parts.updateOne({
    _id: partId
  }, {
    $set: {
      partnumber,
      partName,
      partEcl,
      customer,
      assemblyPartNumber,
      assemblyPartDesc,
      mould,
      status
    }
  });

  if (!updatedPart) {
    res.status(403).json({
      status: "403",
      message: "part not updated",
      body: ""
    });
  }

  res.status(200).json({
    status: "200",
    message: "part updated",
    body: updatedPart
  });
}; //Getting all parts///////////////////////////////////////////////////////////////////////////////////////////////////////////////////


const getParts = async (req, res) => {
  const {
    CompanyId
  } = req.params;

  if (CompanyId.length !== 24) {
    return;
  }

  const company = await Company.find({
    _id: {
      $in: CompanyId
    }
  });

  if (!company) {
    return;
  }

  const parts = await Parts.find({
    company: {
      $in: CompanyId
    }
  }).populate({
    path: "customer"
  }).sort({
    "partnumber": 1
  });
  res.json({
    status: "200",
    message: "Parts loaded",
    body: parts
  });
};

module.exports = {
  createPart,
  udpateParts,
  getParts
};
