  const {
    laptopName,
    responsible,
    location,
    monitor,
    ram,
    osName,
    model,
    system,
    processor,
    serialNo,
    macAddress,
    principalDisk,
    secondaryDisk,
    initialCost,
    purchaseDate,
    status,
    responsibeLetter,
    modifiedBy,
    modified,
    version
  } = req.body;
  const newLaptop = new Laptops({
    laptopName,
    location,
    monitor,
    ram,
    osName,
    model,
    system,
    processor,
    serialNo,
    macAddress,
    principalDisk,
    secondaryDisk,
    initialCost,
    purchaseDate,
    status,
    responsibeLetter,
