        const existingMaterial = await Items.findOne({
          name: material.name
        });

        if (existingMaterial) {
          // Si el material existe, actualizamos la cantidad
          existingMaterial.qty += material.qty;
          await existingMaterial.save();
        } else {
          // Si el material no existe, lo creamos con el objeto valor2
          const newMaterial = new Items({ ...material,
            itemGroup: {
              valor: material.itemGroup,
              // Asignamos el valor
              descripcion: "Desconocido" // Descripción predeterminada

            },
            company: company
          });
          await newMaterial.save();
        }
      }
    }

    await nuevoMovimiento.save();
    res.status(200).json({
      status: "200",
      message: 'Materiales procesados correctamente'
    });
  } catch (error) {
