const updatePedido = async (req, res) => {
  const formatter = new Intl.DateTimeFormat("es-MX", {
    timeZone: "America/Mexico_City",
    hour: "2-digit",
    minute: "2-digit",
    hour12: false
  });
  const parts = formatter.formatToParts(new Date());
  const hour = parts.find(p => p.type === "hour").value;
  const minutes = parts.find(p => p.type === "minute").value;
  const time = `${hour}:${minutes}`;

  try {
    const {
      idPedido
    } = req.params;
    const {
      items,
      surtidor,
      pStatus
    } = req.body.pedido;

    if (!idPedido || !items || !Array.isArray(items)) {
      return res.status(400).json({
        message: "Datos inválidos."
      });
    } // Buscar el pedido actual


    const pedidoActual = await Pedido.findOne({
      idPedido: idPedido
    });

    if (!pedidoActual) {
      return res.status(404).json({
        message: "Pedido no encontrado."
      });
    } // Registrar el movimiento en la base de datos


    const nuevoMovimiento = new RegistroMovimientos({
      tipoAccion: "Surtir pedido",
      detalles: `Surtió el pedido con id: ${idPedido}.`
    }); // Buscar el surtidor

    let surtidorId = null;

    if (surtidor) {
      const foundUsers = await User.find({
        username: surtidor
      });
      surtidorId = foundUsers.map(user => user._id);
      nuevoMovimiento.usuario = surtidorId;
    } // Actualizar los ítems del pedido actual


    pedidoActual.items = items.map(material => ({
      id: material.id,
      // Mantener el ID original
      serial: material.serial,
      // Actualizar los seriales
      quantityReq: material.quantityReq,
      // Mantener el quantityReq original si es necesario
      quantitySur: material.quantitySur // Actualizar cantidades surtidas

    })); // Actualizar otros datos del pedido

    if (surtidorId) {
      pedidoActual.surtidor = surtidorId;
    }

    pedidoActual.surTime = time;
    pedidoActual.pStatus = pStatus; // Guardar cambios en el pedido

    await pedidoActual.save(); // Guardar el movimiento registrado

    await nuevoMovimiento.save(); // Enviar respuesta de éxito

    res.status(200).json({
      status: "200",
      message: "Pedido actualizado con éxito.",
      body: pedidoActual
    });
  } catch (error) {
    res.status(500).json({
      status: "500",
      message: "Error en el servidor.",
      error: error.message
    });
  }
};

const getRecentPedidos = async (req, res) => {
  const {
    CompanyId
  } = req.params; // Valida la longitud del CompanyId (asumiendo que es un ObjectId de MongoDB)

  if (CompanyId.length !== 24) {
    // Es mejor enviar una respuesta en lugar de solo retornar
    return res.status(400).json({
      status: "400",
      message: "Formato de CompanyId inválido."
    });
  }

  try {
    // Primero, verificamos si la compañía existe.
    // Si CompanyId es siempre un único ID, findOne es más apropiado.
    // El uso de $in con un solo valor funciona, pero findOne({_id: CompanyId}) es más directo.
    const company = await Company.findOne({
      _id: CompanyId
    });

    if (!company) {
      // Si la compañía no existe, envía una respuesta apropiada.
      return res.status(404).json({
        status: "404",
        message: "Compañía no encontrada."
      });
    } // Calcula la fecha y hora de hace 24 horas desde el momento actual.


    const twentyFourHoursAgo = new Date(Date.now() - 24 * 60 * 60 * 1000); // Busca los pedidos

    const pedidos = await Pedido.find({
      company: CompanyId,
      // Asumo que 'company' en Pedido es el ObjectId de la compañía.
      // Si CompanyId pudiera ser un array, { $in: CompanyId } sería correcto.
      createdAt: {
        $gte: twentyFourHoursAgo
      } // Filtra los pedidos creados desde hace 24 horas hasta ahora.

    }).sort({
      createdAt: -1
    }) // Ordena los más recientes primero
    .populate({
      path: "usuario",
      select: "employee",
      populate: {
        path: "employee",
        select: "name lastName"
      }
    }).populate({
      path: "surtidor",
      select: "employee",
      populate: {
        path: "employee",
        select: "name lastName"
      }
    }).populate({
      path: "items",
      populate: {
        path: "id",
        select: "name description"
      }
    });
    res.json({
      status: "200",
      message: "Pedidos cargados",
      body: pedidos
    });
  } catch (error) {
    // Manejo de errores generales (e.g., problemas de conexión con la BD)
    console.error("Error al obtener pedidos:", error);
    res.status(500).json({
      status: "500",
      message: "Error interno del servidor al cargar los pedidos."
    });
  }
};

