      const foundUsers = await User.find({
        username: {
          $in: retardos
        }
      });
      minuta.retardos = foundUsers.map(u => u._id);
    } else {
      minuta.retardos = [];
    } // Next Minuta


    if (nextMinuta) {
      const foundUsers = await User.find({
        username: {
          $in: nextMinuta
        }
      });
      minuta.nextMinuta = foundUsers.map(u => u._id);
    } else {
      minuta.nextMinuta = [];
    }

    await minuta.save();
    res.status(200).json({
      status: "200",
      message: "Minuta actualizada correctamente"
    });
  } catch (error) {
    console.error(error);
    res.status(500).json({
      status: "error",
      message: "Error al actualizar la minuta",
      error: error.message
